 .column_35 {
    margin-top: 0; /* Asegura que no haya espacio extra arriba */
    padding-top: 0; /* Elimina cualquier relleno extra */
    line-height: 1.1;
  float: left;
  width: 35%;
}

/* Clear floats after the columns */
.mi-row:after {
 margin:0;
  content: "";
  display: table;
  clear: both;
    
}
.mi-row{
    margin-top: 2px;/* Asegura que no haya espacio extra arriba */
    padding-top: 0; /* Elimina cualquier relleno extra */
    margin-left: 5px;
    margin-right: 5px;

}

.cuadro {
    margin-top: 5px;
    padding: 8px;
    border: 3px solid #1e81b0;
    border-radius: 10px;
    page-break-inside: avoid;
}

.item{
    text-align:center;
  display: block;
  margin: auto;
  width: 50%;
} 


.print-format.print-format-preview {
    padding: 0 !important; /* Elimina el padding */
}
